mapper.insert:
INSERT INTO community (
    id
    , community_kinds_code_name
    , title
    , contents
    , view_count
    , is_open
    , is_temp
    , user_id
    , create_at
    , update_at
) values (
     #{id}
     , #{communityKindsCodeName}
     , #{title}
     , #{contents}
     , #{viewCount}
     , #{isOpen}
     , #{isTemp}
     , #{userId}
     , NOW()
     , NOW()
 )

mapper.update:
UPDATE
    community
SET
    community_kinds_code_name = #{communityKindsCodeName}
    , title = #{title}
    , contents = #{contents}
    , is_open = #{isOpen}
    , is_temp = #{isTemp}
    , update_at = NOW()

    id = #{id}

mapper.select:
SELECT id , community_kinds_code_name , title , contents , view_count , is_open , is_temp , user_id , create_at , update_at FROM community
is_temp = false
LIMIT #{startId}, #{countPerPage}
